import { Injectable } from '@angular/core';
import { Cursos } from '../model/cursos';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable, first, tap } from 'rxjs';
import { Contador } from '../model/contador';

@Injectable({
  providedIn: 'root'
})
export class CursosService {

  private readonly API = 'api/cursos';
  constructor(
    private httpClient: HttpClient
  ) { }

  list(page: number, pageSize: number): Observable<Contador> {
    const adjustedPageNumber = page - 1;

    const params = new HttpParams()
      .set('pageNumber', adjustedPageNumber.toString())
      .set('pageSize', pageSize.toString()); // Add this line

    return this.httpClient.get<Contador>(`${this.API}?${params.toString()}`);
  }

  save(record: Partial<Cursos>){
    if(record._id){
      return this.update(record);
    }
    console.log(record);
    return this.create(record);
  }

  loadById(id: string){
    return this.httpClient.get<Cursos>(`${this.API}/${id}`);
  }

  private create(record: Partial<Cursos>){
    return this.httpClient.post<Cursos>(this.API, record);
  }

  private update(record: Partial<Cursos>){
    return this.httpClient.put<Cursos>(`${this.API}/${record._id}`, record);
  }

  remove(id: string){
    return this.httpClient.delete(`${this.API}/${id}`);
  }
}
