import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, NonNullableFormBuilder, Validators } from '@angular/forms';
import { CursosService } from '../../services/cursos.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { Cursos } from '../../model/cursos';

@Component({
  selector: 'app-cursos-form',
  templateUrl: './cursos-form.component.html',
  styleUrls: ['./cursos-form.component.scss'],

})export class CursosFormComponent {
  form: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private service: CursosService,
    private snackBar: MatSnackBar,
    private location: Location,
    private route: ActivatedRoute
  ) {
    this.form = this.formBuilder.group({
      _id: [''],
      name: ['', [Validators.required, Validators.minLength(5), Validators.maxLength(100)]],
      category: ['', [Validators.required]] // Define 'category' control
    });
  }

  getErrorMessage() {
    const nameControl: AbstractControl | null = this.form.get('name');
    if (nameControl && nameControl.hasError('required')) {
      return 'You must enter a value';
    }
    if (nameControl && (nameControl.hasError('minlength') || nameControl.hasError('maxlength'))) {
      return 'Name should be between 5 and 100 characters';
    }

    return '';
  }

  ngOnInit(): void {
    const curso: Cursos = this.route.snapshot.data['curso'];
    this.form.setValue({
      _id: curso._id,
      name: curso.name,
      category: curso.category // Add 'category' control
    });
  }

  onSubmit() {
    if (this.form.valid) {
      this.service.save(this.form.value)
        .subscribe(
          result => this.onSuccess(),
          error => this.onError()
        );
    }
  }

  onCancel() {
    this.location.back();
  }

  private onError() {
    this.snackBar.open('Erro ao salvar curso', '', { duration: 5000 });
  }

  private onSuccess() {
    this.snackBar.open('Curso salvo com sucesso', '', { duration: 5000 });
    this.onCancel();
  }
}
<mat-card >
  <mat-card-header>
    <mat-card-title>Cadastrar</mat-card-title>
  </mat-card-header>

  <mat-card-content>
    <form [formGroup]="form" class="min-width">
      <div>
        <mat-form-field class="full-width">
          <input matInput placeholder="Nome" formControlName="name" required>
        </mat-form-field>

        <mat-form-field class="full-width">
          <mat-label>Categoria</mat-label>
          <mat-select formControlName="category">
            <mat-option value="null"> </mat-option>
            <mat-option value="front-end">Front-End</mat-option>
            <mat-option value="back-end">Back-End</mat-option>
          </mat-select>
        </mat-form-field>
      </div>

   </form>
  </mat-card-content>
  <mat-card-actions align="start">
    <button mat-raised-button color="primary"  (click)="onSubmit()">Salvar</button>
    <button mat-raised-button  (click)="onCancel()">Cancelar</button>
  </mat-card-actions>
</mat-card>
