import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Cursos } from '../../model/cursos';

@Component({
  selector: 'app-cursos-lista',
  templateUrl: './cursos-lista.component.html',
  styleUrls: ['./cursos-lista.component.scss']
})
export class CursosListaComponent {

  @Input() cursos: Cursos []=[];
  @Output() add = new EventEmitter (false);
  @Output() edit = new EventEmitter (false);
  @Output() remove = new EventEmitter (false);

  displayedColumns = ['name', 'category', 'actions'];

  constructor(

  ){}

  onAdd(){
    //console.log('onAdd');
    this.add.emit(true);
  }

  onEdit(curso: Cursos){
    this.edit.emit(curso);
  }

  onDelete(curso: Cursos){
    this.remove.emit(curso);
  }

}
<mat-table  [dataSource]="cursos">

  <ng-container matColumnDef="name">
    <mat-header-cell  *matHeaderCellDef>Nome</mat-header-cell>
    <mat-cell  *matCellDef="let element">{{ element.name }}</mat-cell>
  </ng-container>

  <ng-container matColumnDef="category">
    <mat-header-cell *matHeaderCellDef>Categoria</mat-header-cell>
    <mat-cell *matCellDef="let element">{{ element.category }}
      <mat-icon aria-hidden="false" aria-label="Categoria do Curso">{{ element.category | category}}</mat-icon>
    </mat-cell>
  </ng-container>

  <ng-container matColumnDef="actions">
    <mat-header-cell *matHeaderCellDef class="action-col add-button">Adicionar
      <button mat-icon-button color="primary" aria-label="Adicionar Curso" (click)="onAdd()" class="action-col-header">
        <mat-icon >add</mat-icon>
      </button>
    </mat-header-cell>

    <mat-cell *matCellDef="let element" class="action-col">
      <button mat-icon-button color="primary" aria-label="Editar" (click)="onEdit(element)">
        <mat-icon >edit</mat-icon>
      </button>
      <button mat-icon-button color="warn" aria-label="Remover" (click)="onDelete(element)">
        <mat-icon>delete</mat-icon>
      </button>
    </mat-cell>
  </ng-container>

  <mat-header-row  *matHeaderRowDef="displayedColumns"></mat-header-row>
  <mat-row  *matRowDef="let row; columns: displayedColumns"></mat-row>
</mat-table>
