import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-cursos-form',
  templateUrl: './cursos-form.component.html',
  styleUrls: ['./cursos-form.component.scss'],

})
export class CursosFormComponent {
form: FormGroup;

constructor(private formBuilder: FormBuilder) {
  this.form = this.formBuilder.group({
    name: [null],
    category: [null]
  });
}

  ngOnInit(): void{

  }

  onSubmit() {
    // You can access form values using this.form.value
    console.log(this.form.value);

    // Add your form submission logic here
  }

}
<mat-card >
  <mat-card-header>
    <mat-card-title>Cadastrar</mat-card-title>
  </mat-card-header>

  <mat-card-content>
    <form [formGroup]="form" >
      <div>
        <mat-form-field class="full-width">
          <input matInput placeholder="Nome" formControlName="name" required>
        </mat-form-field>

        <mat-form-field class="full-width">
          <input matInput placeholder="Categoria" formControlName="category" required>
        </mat-form-field>
      </div>

   </form>
  </mat-card-content>
  <mat-card-actions align="start">
    <button mat-button>LIKE</button>
    <button mat-button>SHARE</button>
  </mat-card-actions>
</mat-card>
