import { Component, OnInit, ViewChild } from '@angular/core';
import { Cursos } from '../../model/cursos';
import { CursosService } from '../../services/cursos.service';
import { Observable, catchError, of } from 'rxjs';
import { MatDialog } from '@angular/material/dialog';
import { ErrorDialogComponent } from 'src/app/shared/components/error-dialog/error-dialog.component';
import { ActivatedRoute, Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ConfirmationDialogComponent } from '../../components/confirmation-dialog/confirmation-dialog.component';
import { MatPaginator, PageEvent } from '@angular/material/paginator';

@Component({
  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.scss'],
})
export class CursosComponent implements OnInit {

  pageIndex = 0;
  pageSize = 10;
  contador = 0;
  @ViewChild(MatPaginator) paginator: MatPaginator | undefined;


  cursos$: Observable<Cursos[]>  | null= null; //declaração e inicialização da variável.
  //cursosService: CursosService;

  constructor(
    private cursosService: CursosService,
    public dialog: MatDialog,
    private router: Router,
    private route: ActivatedRoute,
    private snackBar: MatSnackBar
  ) {
    this.refresh();
  }

  onError(errorMessage: string) {
    this.dialog.open(ErrorDialogComponent, {
      data: errorMessage,
    });
  }

  ngOnInit(): void {}

  refresh() {
    this.cursos$ = this.cursosService.list().pipe(
      catchError((error) => {
        this.onError('Erro ao carregar a tabela');
        return of([]);
      })
    );
  }

  onAdd() {
    console.log('onAdd');
    this.router.navigate(['novo'], { relativeTo: this.route });
  }

  onEdit(curso: Cursos) {
    this.router.navigate(['editar', curso._id], { relativeTo: this.route });
  }

  onDelete(curso: Cursos) {
    const dialogRef = this.dialog.open(ConfirmationDialogComponent, {
      data: 'Tem certeza que deseja remover o curso?',
    });

    dialogRef.afterClosed().subscribe((result: boolean) => {
      if(result){
        this.cursosService.remove(curso._id).subscribe({
      next: () => {
        this.refresh();
        this.snackBar.open('Curso removido com sucesso', 'X', {
          duration: 5000,
          verticalPosition: 'top',
          horizontalPosition: 'center',
        });
      },
      error: error => this.onError('Erro ao remover o curso')
    });
      }
    });

  }

  onPageChange(event: PageEvent) {
    // Update the pageIndex and pageSize when the user interacts with the paginator
    this.pageIndex = event.pageIndex;
    this.pageSize = event.pageSize;

    // You may want to fetch data for the new page here and update the 'contador' accordingly
    // For example, make an API call to retrieve data for the new page
    // and update 'contador' with the total number of elements.
    // This would depend on how you are getting the data.

    // Example:
    // this.service.getData(this.pageIndex, this.pageSize).subscribe((data) => {
    //   this.contador = data.totalElements;
    //   // Update your data source with the new data
    //   // this.dataSource = data.cursos;
    // });
  }

}
<mat-card>

  <mat-toolbar color="secondary"> Cursos Disponíveis </mat-toolbar>
  <div class="column-flex mat-elevation-z8" >
    <app-cursos-lista *ngIf="cursos$ | async as cursos; else loading"
      [cursos]="cursos"
      (add)="onAdd()"
      (edit)="onEdit($event)"
      (remove)="onDelete($event)">

    </app-cursos-lista>

    <!-- Ensure you have the mat-table with the right data source and columns -->
    <table mat-table [dataSource]="cursos" class="mat-elevation-z8">
      <!-- Define your table columns here -->
      <ng-container matColumnDef="_id">
        <th mat-header-cell *matHeaderCellDef>ID</th>
        <td mat-cell *matCellDef="let curso">{{ curso._id }}</td>
      </ng-container>
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef>Name</th>
        <td mat-cell *matCellDef="let curso">{{ curso.name }}</td>
      </ng-container>
      <ng-container matColumnDef="category">
        <th mat-header-cell *matHeaderCellDef>Category</th>
        <td mat-cell *matCellDef="let curso">{{ curso.category }}</td>
      </ng-container>
      <!-- Define other columns as needed -->
      <!-- ... -->
      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
    </table>

    <!-- Paginator -->
    <mat-paginator #paginator
      [length]="contador"
      [pageSize]="pageSize"
      [pageIndex]="pageIndex"
      [pageSizeOptions]="[5, 10, 20, 50]"
      [showFirstLastButtons]="true"
      (page)="onPageChange($event)"
    ></mat-paginator>

    <ng-template #loading>
      <div class="loading-spinner">
        <mat-spinner></mat-spinner>
      </div>
    </ng-template>
  </div>
</mat-card>
