import { Component } from '@angular/core';
import { Cursos } from '../model/cursos';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-cursos-lista',
  templateUrl: './cursos-lista.component.html',
  styleUrls: ['./cursos-lista.component.scss']
})
export class CursosListaComponent {

  @Input() cursos: Cursos []=[];
  displayedColumns = ['name', 'category', 'actions'];

  constructor(
    private router:Router,
    private route: ActivatedRoute
  ){}

  onAdd(){
    console.log('onAdd');
    this.router.navigate(['novo'], {relativeTo: this.route});
  }
}
<table mat-table [dataSource]="cursos" class="mat-elevation-z8">

  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef>Nome</th>
    <td mat-cell *matCellDef="let element">{{ element.name }}</td>
  </ng-container>

  <ng-container matColumnDef="category">
    <th mat-header-cell *matHeaderCellDef>Categoria</th>
    <td mat-cell *matCellDef="let element">{{ element.category }}
      <mat-icon aria-hidden="false" aria-label="Categoria do Curso">{{ element.category | category}}</mat-icon>
    </td>
  </ng-container>

  <ng-container matColumnDef="actions">
    <mat-header-cell *matHeaderCellDef>
      <button mat-mini-fab color="accent" aria-label="Adicionar Curso" (click)="onAdd()">
        <mat-icon >add</mat-icon>
      </button>
    </mat-header-cell>

    <mat-cell *matCellDef="let element">
      <button mat-icon-button color="primary" aria-label="Editar">
        <mat-icon >edit</mat-icon>
      </button>
      <button mat-icon-button color="warn" aria-label="Remover">
        <mat-icon>delete</mat-icon>
      </button>
    </mat-cell>
  </ng-container>



  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
</table>
