import { Component, OnInit } from '@angular/core';
import { Cursos } from '../model/cursos';
import { CursosService } from '../services/cursos.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.scss'],
})
export class CursosComponent implements OnInit {
  cursos$: Observable<Cursos[]>; //declaração e inicialização da variável.
  displayedColumns = ['name', 'category'];
  //cursosService: CursosService;

  constructor(private cursosService: CursosService) {
   // this.cursosService = new CursosService();
    this.cursos$ = this.cursosService.list();
  }

  ngOnInit(): void {}
}
<mat-card>

  <mat-toolbar color="secondary">    Cursos Disponíveis </mat-toolbar>

<div *ngIf="cursos$ | async as cursos; else loading">
  <table mat-table [dataSource]="cursos" class="mat-elevation-z8">
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef>Nome</th>
      <td mat-cell *matCellDef="let element">{{ element.name }}</td>
    </ng-container>

    <ng-container matColumnDef="category">
      <th mat-header-cell *matHeaderCellDef>Categoria</th>
      <td mat-cell *matCellDef="let element">{{ element.category }}</td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
  </table>
</div>
<ng-template #loading>
  <div class="loading-spinner">
  <mat-spinner></mat-spinner>
</div>
</ng-template>
</mat-card>
