import { Component, ViewChild } from '@angular/core';
import {
  FormBuilder,
  FormGroup,
  NonNullableFormBuilder,
  UntypedFormArray,
  Validators,
} from '@angular/forms';
import { CursosService } from '../../services/cursos.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { Cursos } from '../../model/cursos';
import { Aulas } from '../../model/aulas';
import { Subscription } from 'rxjs';
import { FormUtilsService } from 'src/app/shared/form/form-utils.service';
import { MatPaginator, PageEvent } from '@angular/material/paginator';

@Component({
  selector: 'app-cursos-form',
  templateUrl: './cursos-form.component.html',
  styleUrls: ['./cursos-form.component.scss'],
})
export class CursosFormComponent {
  form: FormGroup;

  pageIndex = 0;
  pageSize = 10;
  contador = 0;
  @ViewChild(MatPaginator) paginator: MatPaginator | undefined;

  constructor(
    private formBuilder: FormBuilder,
    private service: CursosService,
    private snackBar: MatSnackBar,
    private location: Location,
    private route: ActivatedRoute,
    public formUtils: FormUtilsService
  ) {
    this.form = this.formBuilder.group({
      _id: [''],
      name: [
        '',
        [
          Validators.required,
          Validators.minLength(3),
          Validators.maxLength(100),
        ],
      ],
      category: ['', [Validators.required]],
    });
  }

  ngOnInit(): void {
    const curso: Cursos = this.route.snapshot.data['curso'];
    this.form = this.formBuilder.group({
      _id: [curso._id],
      name: [
        curso.name,
        [
          Validators.required,
          Validators.minLength(2),
          Validators.maxLength(100),
        ],
      ],
      category: [curso.category, [Validators.required]],
      aulas: this.formBuilder.array(
        this.obterAulas(curso),
        Validators.required
      ),
    });
  }

  private obterAulas(curso: Cursos) {
    const aulas = [];
    if (curso?.aulas) {
      curso.aulas.forEach((aula) => aulas.push(this.criarAula(aula)));
    } else {
      aulas.push(this.criarAula());
    }
    return aulas;
  }

  private criarAula(aula: Aulas = { id: '', name: '', youtubeUrl: '' }) {
    return this.formBuilder.group({
      id: [aula.id],
      name: [
        aula.name,
        [
          Validators.required,
          Validators.minLength(5),
          Validators.maxLength(100),
        ],
      ],
      youtubeUrl: [
        aula.youtubeUrl,
        [
          Validators.required,
          Validators.minLength(5),
          Validators.maxLength(100),
        ],
      ],
    });
  }

  onSubmit() {
    // Check if the form is valid and category is not null or "null"
    if (
      this.form.valid &&
      this.form.value.category &&
      this.form.value.category !== 'null'
    ) {
      this.service.save(this.form.value).subscribe({
        next: (result) => this.onSuccess(),
        error: (error) => this.onError(),
      });
    } else {
      this.formUtils.validateAllFormFields(this.form);
      this.snackBar.open(
        'Por favor, preencha todos os campos corretamente.',
        '',
        { duration: 5000 }
      );
    }
  }

  onCancel() {
    this.location.back();
  }

  private onError() {
    this.snackBar.open('Erro ao salvar curso', '', { duration: 5000 });
  }

  private onSuccess() {
    this.snackBar.open('Curso salvo com sucesso', '', { duration: 5000 });
    this.onCancel();
  }

  getAulasFormArray() {
    return (<UntypedFormArray>this.form.get('aulas')).controls;
  }

  addAula() {
    const aulas = this.form.get('aulas') as UntypedFormArray;
    aulas.push(this.criarAula());
  }

  removeAula(index: number) {
    const aulas = this.form.get('aulas') as UntypedFormArray;
    aulas.removeAt(index);
  }
  onPageChange(event: PageEvent) {
    // Update the pageIndex and pageSize when the user interacts with the paginator
    this.pageIndex = event.pageIndex;
    this.pageSize = event.pageSize;

    // You may want to fetch data for the new page here and update the 'contador' accordingly
    // For example, make an API call to retrieve data for the new page
    // and update 'contador' with the total number of elements.
    // This would depend on how you are getting the data.

    // Example:
    // this.service.getData(this.pageIndex, this.pageSize).subscribe((data) => {
    //   this.contador = data.totalElements;
    //   // Update your data source with the new data
    //   // this.dataSource = data.cursos;
    // });
  }
}
<mat-card>
  <mat-card-header>
    <mat-card-title>Cadastrar</mat-card-title>
  </mat-card-header>

  <mat-card-content>
    <form [formGroup]="form" class="min-width">
      <div>
        <mat-form-field class="full-width">
          <input matInput placeholder="Nome" formControlName="name" #name required>
          <mat-hint align="end">{{name.value.length}}/100</mat-hint>

          <mat-error *ngIf="form.get('name')?.invalid">{{
            formUtils.getErrorMessage(form, 'name')
          }}</mat-error>
        </mat-form-field>

        <mat-form-field class="full-width">
          <mat-label>Categoria</mat-label>
          <mat-select formControlName="category">
            <mat-option value="Front-end">Front-End</mat-option>
            <mat-option value="Back-end">Back-End</mat-option>
          </mat-select>
          <mat-error *ngIf="form.get('category')?.invalid && form.get('category')?.touched">
            Categoria é obrigatória
          </mat-error>
        </mat-form-field>

        <mat-toolbar>
          <div class="align-left">
            <span>Aulas</span>
          </div>
          <div class="align-right add-button"> Adicionar
            <button mat-icon-button (click)="addAula()" color="primary">
              <mat-icon aria-label="Adicionar uma nova aula">add</mat-icon>
            </button>
          </div>
        </mat-toolbar>

        <mat-error *ngIf="formUtils.isFormArrayRequired(form, 'aulas')"
        class="align-left"> Adicione uma aula </mat-error>

        <table formArrayName="aulas"
        *ngFor="let a of getAulasFormArray();
        let i = index ">

          <tr [formGroupName]="i">
            <td style="width: 65%">
              <mat-form-field class="full-width">
                <mat-label> Título Aula</mat-label>
                <input matInput formControlName="name">
                <mat-error *ngIf="getAulasFormArray()[i].get('name')?.
                invalid">{{ formUtils.getFormArrayFieldsErrorMessage(form, 'aulas', 'name', i)}}</mat-error>

              </mat-form-field>
            </td>
            <td style="width: 30%">
              <mat-form-field class="full-width">
                <mat-label> URL Aula</mat-label>
                <span matPrefix>http://youtube.be/</span>
                <input matInput formControlName="youtubeUrl">
                <mat-error *ngIf="getAulasFormArray()[i].get('youtubeUrl')?.
                invalid">{{ formUtils.getFormArrayFieldsErrorMessage(form, 'aulas', 'youtubeUrl', i)}}</mat-error>
              </mat-form-field>
            </td>
            <td>
              <mat-icon aria-label="Remover aula" (click)="removeAula(i)"  color="warn">delete</mat-icon>
            </td>
          </tr>
        </table>

      </div>

    </form>

  </mat-card-content>
