import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-cursos-form',
  templateUrl: './cursos-form.component.html',
  styleUrls: ['./cursos-form.component.scss'],

})
export class CursosFormComponent {
form: FormGroup;

constructor(private formBuilder: FormBuilder) {
  this.form = this.formBuilder.group({
    name: [null],
    category: [null]
  });
}

  ngOnInit(): void{

  }

  onSubmit() {
    // You can access form values using this.form.value
    console.log(this.form.value);

    // Add your form submission logic here
  }
  onCancel() {
    // You can access form values using this.form.value
    console.log(this.form.value);

    // Add your form submission logic here
  }

}
<mat-card >
  <mat-card-header>
    <mat-card-title>Cadastrar</mat-card-title>
  </mat-card-header>

  <mat-card-content>
    <form [formGroup]="form" >
      <div>
        <mat-form-field class="full-width">
          <input matInput placeholder="Nome" formControlName="name" required>
        </mat-form-field>

        <mat-form-field class="full-width">
          <mat-label>Categoria</mat-label>
          <mat-select formControlName="category">
            <mat-option value="option1">Option 1</mat-option>
            <mat-option value="option2" disabled>Option 2 (disabled)</mat-option>
            <mat-option value="option3">Option 3</mat-option>
          </mat-select>
          <input matInput   required>
        </mat-form-field>
      </div>

   </form>
  </mat-card-content>
  <mat-card-actions align="start">
    <button mat-raised-button color="primary"  (click)="onSubmit()">Salvar</button>
    <button mat-raised-button  (click)="onCancel()">Cancelar</button>
  </mat-card-actions>
</mat-card>
